import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { Link, useNavigate, useParams } from 'react-router-dom';
import { addUser } from './UserReducer';

function Update() {
    const {id} = useParams();
    const users  = useSelector((state) =>state.users);
    const existingUser = users.filter(f => f.id == id)
    const [name , email] = existingUser[0]
    const [upname ,setUpName] = useState()
    const [upemail ,setUpEmail] = useState('')


    const dispatch = useDispatch();
    const navigate = useNavigate();
     const handlesubmit = (event) => {
        event.preventDefault();
        dispatch(addUser( {
            id: users[users.length-1].id+1,
            language: upname,
            name: upemail,
            version: users[users.length-1].version+0.2,
     }))
     navigate('/')
    }
  return (
    <div>
        <h3>update User</h3>
        <form onSubmit={handlesubmit}>
            <div>
                <label htmlFor = 'name'>Name : </label>
               <input type = "text" name = {upname} placeholder='enter name' 
               onChange={e => setUpName( e.target.value)}/>
            </div>

            <div>
                <label htmlFor = 'email'>Email : </label>
               <input type = "email" email  = {upemail} placeholder='enter email'
               onChange = {e => setUpEmail(e.target.value)}/>
            </div>
            <button className='btn btn-primary my-3'>update</button>
            <Link to = "/" className='btn btn-success my-3'>Back</Link>

        </form>
    </div>
  )
}

export default Update
