import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom';
import { deleteUser, searchUser } from './UserReducer';



function Home() {
    const users  = useSelector((state) =>state.users);
    const dispatch = useDispatch();
    const handledelete = (id) =>{
      dispatch(deleteUser({id: id}))
    const handleSearch = () => {
      dispatch(searchUser())
    }
    }
  return (
    <div>

        <h2>user list </h2>
        < Link to = "/Create" className='btn btn-success m-3'>create</Link>
        <label for="gsearch">Search User:</label>
        // eslint-disable-next-line no-undef
        <input  type="search" onChange={handleSearch()} id="gsearch" name="gsearch" ></input>
  <table className='table'>
                <tr>
                    <th>Id</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Version</th>
                    <th>actions</th>
                </tr>
              {users.map((user, index) =>  (
                <><tr key={index}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{user.version}</td>
                  <td>
                    <Link to = {`/edit/${user.id}` }className='btn btn-primary m-1'>edit</Link>
                    <button onClick={() => handledelete(user.id)} className='btn btn-danger m-1'>delete</button>
                  </td>
                </tr></>
              ))}
   </table>

 </div>
  )
}

export default Home
