import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom';
import { deleteUser, searchUser } from './UserReducer';



function Home() {
    const users  = useSelector((state) =>state.users);
    const dispatch = useDispatch();

    const handledelete = (id) =>{
      dispatch(deleteUser({id: id}));
    }

    const handleSearch = (value) => {
      var input, filter, table, tr, td, i, txtValue;
  input = document.getElementById("myInput");
  filter = input.value.toUpperCase();
  table = document.getElementById("myTable");
  tr = table.getElementsByTagName("tr");
  for (i = 0; i < tr.length; i++) {
    td = tr[i].getElementsByTagName("td")[0];
    if (td) {
      txtValue = td.textContent || td.innerText;
      if (txtValue.toUpperCase().indexOf(filter) > -1) {
        tr[i].style.display = "";
      } else {
        tr[i].style.display = "none";
      }
    }       
  }
      // dispatch(searchUser({id: value}));
    }

  return (
    <div>

        <h2>user list </h2>
        < Link to = "/Create" className='btn btn-success m-3'>create</Link>
        <label for="gsearch">Search User:</label>
        <input type='text' placeholder='Search' id="myInput" name="search" onChange={(e) => handleSearch(e.target.value)}></input>
  <table className='table' id="">
                <tr>
                    <th>Id</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Version</th>
                    <th>actions</th>
                </tr>
              {users.map((user, index) =>  (
                <><tr key={index}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{user.version}</td>
                  <td>
                    <Link to = {`/edit/${user.id}` }className='btn btn-primary m-1'>edit</Link>
                    <button onClick={() => handledelete(user.id)} className='btn btn-danger m-1'>delete</button>
                  </td>
                </tr></>
              ))}
   </table>

 </div>
  )
}

export default Home
